import React from "react";
import _ from "lodash";
import robot from "../robot.png";
import target from "../target.png";

import Image from "react-bootstrap/Image";

function Square(props) {
  const {
    rowPosition,
    columnPosition,
    robotPosition,
    targetPosition,
    direction,
  } = props;

  const currentPosition = [rowPosition, columnPosition];

  if (direction === "N") {
    const robotImage = <Image src={robot} className="robot up"></Image>;
  } else if (direction === "E") {
    const robotImage = <Image src={robot} className="robot right"></Image>;
  }

  if (_.isEqual(robotPosition, currentPosition)) {
    return (
      <>
        <td className="board-cell">
          <Image src={robot} className="robot" />
        </td>
      </>
    );
  } else if (_.isEqual(targetPosition, currentPosition)) {
    return (
      <>
        <td className="board-cell">
          <Image src={target} className="target" />
        </td>
      </>
    );
  } else {
    return (
      <>
        <td className="board-cell"></td>
      </>
    );
  }
}

export default Square;
