import React, { useState } from "react";
import Board from "./Board";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUndo, faRedo, faArrowUp } from "@fortawesome/free-solid-svg-icons";

function Game() {
  const rows = [0, 1, 2, 3, 4];
  const columns = [0, 1, 2, 3, 4];

  const directions = ["N", "E", "S", "W"];
  const randomDirection =
    directions[Math.floor(Math.random() * directions.length)];

  const [direction, setDirection] = useState(randomDirection);
  const [robotPosition, setRobotPosition] = useState([0, 0]);
  const [targetPosition, setTargetPosition] = useState([3, 0]);

  const rotateLeftIcon = <FontAwesomeIcon icon={faUndo} />;
  const rotateRightIcon = <FontAwesomeIcon icon={faRedo} />;
  const arrowUp = <FontAwesomeIcon icon={faArrowUp} />;

  const rotateRight = () => {
    const currentDirection = directions.findIndex((element) => {
      return element === direction;
    });
  };

  return (
    <>
      <Row className="pt-5">
        <Col>
          <Board
            rows={rows}
            columns={columns}
            targetPosition={targetPosition}
            robotPosition={robotPosition}
            direction={direction}
          ></Board>
        </Col>
      </Row>
      <Row className="pt-5">
        <Col>
          <div className="d-grid gap-2">
            <Button variant="primary">{rotateLeftIcon}</Button>
          </div>
        </Col>
        <Col>
          <div className="d-grid gap-2">
            <Button variant="primary">{arrowUp}</Button>
          </div>
        </Col>
        <Col>
          <div className="d-grid gap-2">
            <Button variant="primary">{rotateRightIcon}</Button>
          </div>
        </Col>
      </Row>
    </>
  );
}

export default Game;
