import React, { useState } from "react";
import Board from "./Board";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUndo, faRedo } from "@fortawesome/free-solid-svg-icons";

function Game() {
  const rows = [0, 1, 2, 3, 4];
  const columns = [0, 1, 2, 3, 4];

  const [direction, setDirection] = useState(0);
  const [robotPosition, setRobotPosition] = useState([0, 0]);
  const [targetPosition, setTargetPosition] = useState([3, 0]);

  const rotateLeft = <FontAwesomeIcon icon={faUndo} />;
  const rotateRight = <FontAwesomeIcon icon={faRedo} />;

  return (
    <>
      <Row className="pt-5">
        <Col>
          <Board
            rows={rows}
            columns={columns}
            targetPosition={targetPosition}
            robotPosition={robotPosition}
          ></Board>
        </Col>
      </Row>
      <Row className="pt-5">
        <Col>
          <Button variant="primary">{rotateLeft}</Button>
        </Col>
        <Col>
          <Button variant="primary">{rotateRight}</Button>
        </Col>
      </Row>
    </>
  );
}

export default Game;
